import React from "react";
import { connect } from "react-redux";
import { deleteStaff } from "../../actions/staffActions";
import PropTypes from "prop-types";
import M from "materialize-css/dist/js/materialize.min.js";

const StaffItem = ({ staff, deleteStaff }) => {
  return (
    <li className="collection-item">
      <div>
        {staff.firstName} {staff.lastName}
        <a
          href="#!"
          className="secondary-content"
          onClick={() =>
            deleteStaff(staff.id).then(() => {
              M.toast({
                html: `"${staff.firstName} ${staff.lastName}" was removed from the staff`,
              });
            })
          }
        >
          <i className="material-icons grey-text">delete</i>
        </a>
      </div>
    </li>
  );
};

StaffItem.propTypes = {
  staff: PropTypes.object.isRequired,
};

export default connect(null, { deleteStaff })(StaffItem);
